import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import { Input } from '@material-ui/core';
import {Link} from 'react-router-dom'
import axios from 'axios'
class Login extends React.Component {
    state={
        email:'',
        password:'',
        errorLog:''
    }

  componentDidMount(){
      window.localStorage.removeItem('application_data')
  }
  changeHandler=(event)=>{
    event.preventDefault()
    this.setState({
      [event.target.name]:event.target.value
    })
  }
  submitHandler=(event)=>{
      const {email, password}=this.state
      axios.post('/login',{email, password})
      .then(res=>{
          localStorage.setItem('application_data',res.data)
          window.location.href='/home'
      })
      .catch(err=>{
          this.setState({
              errorLog:err.response.data.error
          })
          console.log(err.response.data.error)
      })
    }
    render(){
        return(
            <div className="col-md-4 offset-md-4">
                <Card  className="mt-5">
                    <CardActionArea>
                        <CardContent>
                            <form >
                                <h3>Login </h3>
                                <div className="md-form">
    <i className="fas fa-user prefix"></i>
    <input type="text" id="inputIconEx2" className="form-control" />
    <label htmlFor="inputIconEx2">E-mail address</label>
  </div>

                            </form>
                        </CardContent>
                    </CardActionArea>
                    <CardActions>
                        <Button size="small" color="primary" onClick={this.submitHandler}>Login</Button>
                        <Link className="ml-3" to ='/signup'>Sign Up</Link>
                    </CardActions>
                </Card>
            </div>
        )
    }
}

export default Login
