import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import { Input } from '@material-ui/core';
import {Link} from 'react-router-dom'
import axios from 'axios'
class Login extends React.Component {
    state={
        email:'',
        password:'',
        errorLog:''
    }

  componentDidMount(){
      window.localStorage.removeItem('application_data')
  }
  changeHandler=(event)=>{
    event.preventDefault()
    this.setState({
      [event.target.name]:event.target.value
    })
  }
  submitHandler=(event)=>{
      const {email, password}=this.state
      axios.post('/login',{email, password})
      .then(res=>{
          localStorage.setItem('application_data',res.data)
          window.location.href='/home'
      })
      .catch(err=>{
          this.setState({
              errorLog:err.response.data.error
          })
          console.log(err.response.data.error)
      })
    }
    render(){
        return(
            <div className="col-md-4 offset-md-4">
                <Card  className="mt-5">
                    <CardActionArea>


                                <div className="modal fade" id="modalContactForm" tabindex="-1" role="dialog" aria-labelledby="myModalLabel"
    aria-hidden="true">
    <div className="modal-dialog cascading-modal" role="document">

      <div className="modal-content">

        <div className="modal-header light-blue darken-3 white-text">
          <h4 className="title"><i className="fas fa-pencil-alt"></i>Sign In</h4>
          <button type="button" className="close waves-effect waves-light" data-dismiss="modal" aria-label="Close">
            <span aria-hidden="true">×</span>
          </button>
        </div>

        <div className="modal-body mb-0">
          <div className="md-form form-sm">
            <i className="fas fa-envelope prefix active"></i>
            <input type="text" id="form8" className="form-control" />
            <label htmlFor="form8" className="active">Your name</label>
          </div>

          <div className="md-form form-sm">
            <i className="fas fa-lock prefix active"></i>
            <input type="password" id="form9" className="form-control" />
            <label htmlFor="form9" className="active">Your email</label>
          </div>




          <div className="text-center mt-1-half">
            <button className="btn btn-info mb-2 waves-effect waves-light">Send <i className="fas fa-send ml-1"></i></button>
          </div>

        </div>
      </div>

    </div>
  </div>




                    </CardActionArea>
                    <CardActions>
                        <Button size="small" color="primary" onClick={this.submitHandler}>Login</Button>
                        <Link className="ml-3" to ='/signup'>Sign Up</Link>
                    </CardActions>
                </Card>
            </div>
        )
    }
}

export default Login
